import React, { useState, useEffect } from 'react';
import axios from 'axios';

function HomePage() {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [scheduleDate, setScheduleDate] = useState(new Date().toISOString().split('T')[0]);
  const [flightDirection, setFlightDirection] = useState('A');

  useEffect(() => {
    fetchFlights();
  }, [scheduleDate, flightDirection]);

  const fetchFlights = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(`http://localhost:5000/api/flights`, {
        params: { scheduleDate, flightDirection }
      });
      setFlights(response.data.flights);
    } catch (err) {
      setError('Failed to fetch flights. ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Flight Information</h1>
      <div>
        <label>
          Date:
          <input
            type="date"
            value={scheduleDate}
            onChange={(e) => setScheduleDate(e.target.value)}
          />
        </label>
        <label>
          Direction:
          <select
            value={flightDirection}
            onChange={(e) => setFlightDirection(e.target.value)}
          >
            <option value="A">Arrivals</option>
            <option value="D">Departures</option>
          </select>
        </label>
      </div>
      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}
      {flights.length > 0 && (
        <ul>
          {flights.map((flight) => (
            <li key={flight.id}>{flight.flightName} - {flight.route.destinations[0]}</li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default HomePage;
