import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './HomePage.css';

function HomePage() {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [scheduleDate, setScheduleDate] = useState('');
  const [flightDirection, setFlightDirection] = useState('');
  const [page, setPage] = useState(0);

  const fetchFlights = useCallback(async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5000/api/flights', {
        params: { scheduleDate, flightDirection, page }
      });
      console.log('API Response:', response.data);
      setFlights(response.data.flights || []);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching flights:', error.response?.data || error);
      setError(`Failed to fetch flights. ${error.response?.data?.message || error.message}`);
      setLoading(false);
    }
  }, [scheduleDate, flightDirection, page]);

  useEffect(() => {
    fetchFlights();
  }, [fetchFlights]);

  const handleSearch = () => {
    setPage(0);
    fetchFlights();
  };

  const handleNextPage = () => {
    setPage(prevPage => prevPage + 1);
  };

  const handlePrevPage = () => {
    setPage(prevPage => Math.max(0, prevPage - 1));
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="homepage">
      <h1>Flight Information</h1>
      <div className="filters">
        <input 
          type="date" 
          value={scheduleDate} 
          onChange={(e) => setScheduleDate(e.target.value)}
        />
        <select value={flightDirection} onChange={(e) => setFlightDirection(e.target.value)}>
          <option value="">All Directions</option>
          <option value="A">Arrivals</option>
          <option value="D">Departures</option>
        </select>
        <button onClick={handleSearch}>Search Flights</button>
      </div>
      <div className="flight-grid">
        {flights.map(flight => (
          <div key={flight.id} className="flight-card">
            <div className="flight-header">
              <span className="flight-name">{flight.flightName}</span>
              <span className="flight-status">{flight.publicFlightState.flightStates[0]}</span>
            </div>
            <div className="flight-info">
              <p><strong>To:</strong> {flight.route.destinations[0]}</p>
              <p><strong>Scheduled:</strong> {new Date(flight.scheduleDateTime).toLocaleString()}</p>
              <p><strong>Direction:</strong> {flight.flightDirection === 'A' ? 'Arrival' : 'Departure'}</p>
              <p><strong>Terminal:</strong> {flight.terminal}</p>
              <p><strong>Gate:</strong> {flight.gate || 'TBA'}</p>
            </div>
          </div>
        ))}
      </div>
      <div className="pagination">
        <button onClick={handlePrevPage} disabled={page === 0}>Previous Page</button>
        <button onClick={handleNextPage}>Next Page</button>
      </div>
    </div>
  );
}

export default HomePage;
