import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './HomePage.css';

function HomePage() {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/flights')
      .then(response => {
        setFlights(Array.isArray(response.data.flights) ? response.data.flights.slice(0, 10) : []);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching flights:', error);
        setError('Failed to fetch flights. Please try again later.');
        setLoading(false);
      });
  }, []);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="homepage">
      <div className="departure-board">
        <div className="board-header">
          <h1>Departures</h1>
        </div>
        <div className="board-content">
          <div className="board-row header">
            <div className="cell">Flight</div>
            <div className="cell">Destination</div>
            <div className="cell">Time</div>
            <div className="cell">Status</div>
          </div>
          {flights.map(flight => (
            <div key={flight.id} className="board-row">
              <div className="cell">{flight.flightName}</div>
              <div className="cell">{flight.route.destinations[0]}</div>
              <div className="cell">{new Date(flight.scheduleTime).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}</div>
              <div className="cell">{flight.publicFlightState.flightStates[0]}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default HomePage;
