import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './HomePage.css';

function HomePage() {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/flights')
      .then(response => {
        setFlights(Array.isArray(response.data.flights) ? response.data.flights : []);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching flights:', error);
        setError('Failed to fetch flights. Please try again later.');
        setLoading(false);
      });
  }, []);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="homepage">
      <h1>Flight Information</h1>
      <div className="flight-board">
        <div className="flight-row header">
          <div>Flight</div>
          <div>Destination</div>
          <div>Time</div>
          <div>Status</div>
        </div>
        {flights.length > 0 ? (
          flights.map(flight => (
            <div key={flight.id} className="flight-row">
              <div>{flight.flightName}</div>
              <div>{flight.route.destinations[0]}</div>
              <div>{new Date(flight.scheduleTime).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}</div>
              <div className={`status ${flight.publicFlightState.flightStates[0].toLowerCase()}`}>
                {flight.publicFlightState.flightStates[0]}
              </div>
            </div>
          ))
        ) : (
          <div className="no-flights">No flights available at the moment.</div>
        )}
      </div>
    </div>
  );
}

export default HomePage;
