import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './HomePage.css'; // We'll create this file for styling

function HomePage() {
  const [flights, setFlights] = useState([]);
  const [upcomingFlights, setUpcomingFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [scheduleDate, setScheduleDate] = useState(new Date().toISOString().split('T')[0]);
  const [flightDirection, setFlightDirection] = useState('A');
  const [page, setPage] = useState(0);
  const [pagination, setPagination] = useState({ currentPage: 0, totalPages: 1 });

  useEffect(() => {
    fetchFlights();
    fetchUpcomingFlights();
  }, [scheduleDate, flightDirection, page]);

  const fetchFlights = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(`http://localhost:5000/api/flights`, {
        params: { scheduleDate, flightDirection, page }
      });
      console.log('Raw flight data:', response.data.flights);
      setFlights(response.data.flights || []);
      setPagination(response.data.pagination || { currentPage: 0, totalPages: 1 });
    } catch (err) {
      setError('Failed to fetch flights. ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchUpcomingFlights = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/upcoming-flights`);
      setUpcomingFlights(response.data);
    } catch (err) {
      console.error('Failed to fetch upcoming flights:', err);
    }
  };

  const handleNextPage = () => {
    setPage(prevPage => prevPage + 1);
  };

  const handlePrevPage = () => {
    setPage(prevPage => Math.max(0, prevPage - 1));
  };

  const formatTime = (dateString) => {
    if (!dateString) return 'N/A';
    const date = new Date(dateString);
    if (isNaN(date.getTime())) return 'Invalid Date';
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <div className="homepage">
      <h1>Flight Information</h1>
      
      {upcomingFlights.length > 0 && (
        <div className="upcoming-flights">
          <h2>Upcoming Flights</h2>
          <ul>
            {upcomingFlights.map((flight) => (
              <li key={flight.id}>
                <strong>{flight.flightName}</strong> ({flight.flightNumber})
                <br />
                To: {flight.route.destinations[0]}
                <br />
                Departure: {formatTime(flight.scheduleTime)}
                <br />
                Gate: {flight.gate || 'TBA'}
              </li>
            ))}
          </ul>
        </div>
      )}

      <div className="filters">
        <label>
          Date:
          <input
            type="date"
            value={scheduleDate}
            onChange={(e) => setScheduleDate(e.target.value)}
          />
        </label>
        <label>
          Direction:
          <select
            value={flightDirection}
            onChange={(e) => setFlightDirection(e.target.value)}
          >
            <option value="A">Arrivals</option>
            <option value="D">Departures</option>
          </select>
        </label>
      </div>
      {loading && <p className="loading">Loading...</p>}
      {error && <p className="error">{error}</p>}
      {flights.length > 0 && (
        <div className="flights-list">
          <table>
            <thead>
              <tr>
                <th>Flight</th>
                <th>Number</th>
                <th>Destination</th>
                <th>Scheduled Time</th>
                <th>Gate</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {flights.map((flight) => (
                <tr key={flight.id}>
                  <td>{flight.flightName}</td>
                  <td>{flight.flightNumber}</td>
                  <td>{flight.route.destinations[0]}</td>
                  <td>{formatTime(flight.scheduleTime)}</td>
                  <td>{flight.gate || 'TBA'}</td>
                  <td>{flight.publicFlightState.flightStates[0]}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="pagination">
            <button onClick={handlePrevPage} disabled={page === 0}>Previous</button>
            <span>Page {pagination.currentPage + 1} of {pagination.totalPages}</span>
            <button onClick={handleNextPage} disabled={pagination.currentPage + 1 >= pagination.totalPages}>Next</button>
          </div>
          <p className="pagination-info">
            Showing page {pagination.currentPage + 1} of {pagination.totalPages}
          </p>
        </div>
      )}
      {!loading && !error && flights.length === 0 && (
        <p className="no-flights">No flights found for the selected criteria.</p>
      )}
    </div>
  );
}

export default HomePage;
