import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './HomePage.css';

function HomePage() {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [date, setDate] = useState('');
  const [direction, setDirection] = useState('');

  const fetchFlights = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/flights', {
        params: { date, direction }
      });
      setFlights(Array.isArray(response.data.flights) ? response.data.flights : []);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching flights:', error);
      setError('Failed to fetch flights. Please try again later.');
      setLoading(false);
    }
  }, [date, direction]);

  useEffect(() => {
    fetchFlights();
  }, [fetchFlights]);

  const reserveFlight = async (flightId) => {
    try {
      await axios.post('http://localhost:5000/api/reservations', { flightId });
      alert('Your flight has been saved!');
    } catch (error) {
      console.error('Error reserving flight:', error);
      alert('Failed to reserve flight. Please try again.');
    }
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="homepage">
      <h1>Flight Information</h1>
      <div className="filters">
        <input 
          type="date" 
          value={date} 
          onChange={(e) => setDate(e.target.value)}
        />
        <select value={direction} onChange={(e) => setDirection(e.target.value)}>
          <option value="">All Directions</option>
          <option value="departure">Departures</option>
          <option value="arrival">Arrivals</option>
        </select>
      </div>
      <div className="flight-grid">
        {flights.map(flight => (
          <div key={flight.id} className="flight-card">
            <div className="flight-header">
              <span className="flight-name">{flight.flightName}</span>
              <span className="flight-status">{flight.publicFlightState.flightStates[0]}</span>
            </div>
            <div className="flight-info">
              <p><strong>To:</strong> {flight.route.destinations[0]}</p>
              <p><strong>Scheduled:</strong> {new Date(flight.scheduleTime).toLocaleString()}</p>
              <button 
                onClick={() => reserveFlight(flight.id)}
                disabled={new Date(flight.scheduleTime) < new Date()}
              >
                Reserve
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
