import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './HomePage.css';

function HomePage() {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/flights')
      .then(response => {
        setFlights(Array.isArray(response.data.flights) ? response.data.flights : []);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching flights:', error);
        setError('Failed to fetch flights. Please try again later.');
        setLoading(false);
      });
  }, []);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="homepage">
      <h1>Flight Information</h1>
      <div className="flight-grid">
        {flights.length > 0 ? (
          flights.map(flight => (
            <div key={flight.id} className="flight-card">
              <div className="flight-header">
                <span className="flight-name">{flight.flightName}</span>
                <span className="flight-status">{flight.publicFlightState.flightStates[0]}</span>
              </div>
              <div className="flight-info">
                <p><strong>To:</strong> {flight.route.destinations.join(', ')}</p>
                <p><strong>Scheduled:</strong> {new Date(flight.scheduleTime).toLocaleString()}</p>
                <p><strong>Terminal:</strong> {flight.terminal || 'N/A'}</p>
                <p><strong>Gate:</strong> {flight.gate || 'N/A'}</p>
              </div>
            </div>
          ))
        ) : (
          <p className="no-flights">No flights available at the moment.</p>
        )}
      </div>
    </div>
  );
}

export default HomePage;
