import React, { useState, useEffect } from 'react';
import axios from 'axios';

function HomePage() {
  const [flights, setFlights] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/flights/schiphol')
      .then(response => setFlights(response.data))
      .catch(error => console.error('Error fetching flights:', error));
  }, []);

  const reserveFlight = (flight) => {
    axios.post('http://localhost:5000/flights/reserve', {
      flightNumber: flight.flightNumber,
      departureTime: flight.departureTime,
      arrivalTime: flight.arrivalTime,
      origin: flight.origin,
      destination: flight.destination,
      userId: '123', // Assume we have a user ID
    })
      .then(response => {
        alert('Flight reserved successfully!');
        // You might want to redirect to MyFlights page here
      })
      .catch(error => console.error('Error reserving flight:', error));
  };

  return (
    <div>
      <h1>Flight Information</h1>
      {flights.map(flight => (
        <div key={flight.id}>
          {/* Display flight information */}
          <button onClick={() => reserveFlight(flight)}>Reserve</button>
        </div>
      ))}
    </div>
  );
}

export default HomePage;
