import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './HomePage.css';

function HomePage() {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/flights')
      .then(response => {
        setFlights(Array.isArray(response.data.flights) ? response.data.flights : []);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching flights:', error);
        setError('Failed to fetch flights. Please try again later.');
        setLoading(false);
      });
  }, []);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="homepage">
      <h1>Schiphol Airport Flight Information</h1>
      {flights.length > 0 ? (
        <table className="flight-table">
          <thead>
            <tr>
              <th>Flight</th>
              <th>Destination</th>
              <th>Scheduled Time</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {flights.map(flight => (
              <tr key={flight.id}>
                <td>{flight.flightName}</td>
                <td>{flight.route.destinations.join(', ')}</td>
                <td>{new Date(flight.scheduleTime).toLocaleString()}</td>
                <td>{flight.publicFlightState.flightStates.join(', ')}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="no-flights">No flights available at the moment.</p>
      )}
    </div>
  );
}

export default HomePage;
